// Abid Hassan Options Strategy Implementation
const marketData = $json;

// Determine content type based on market conditions
function determineContentType(data) {
  const hour = new Date().getHours();
  const dayOfWeek = new Date().getDay();

  // Market volatility check
  const volatility = Math.abs(data[0].change_percent);

  if (volatility > 2) {
    return 'volatility_analysis';
  } else if (hour === 9 && dayOfWeek !== 0 && dayOfWeek !== 6) {
    return 'market_open_analysis';
  } else if (hour === 15 && dayOfWeek !== 0 && dayOfWeek !== 6) {
    return 'market_close_summary';
  } else if (dayOfWeek === 1 && hour === 8) {
    return 'weekly_outlook';
  } else if (dayOfWeek === 5 && hour === 16) {
    return 'weekly_review';
  } else {
    // Rotate through different content types
    const types = [
      'educational_options',
      'market_analysis',
      'sector_focus',
      'options_strategy',
      'risk_management'
    ];
    return types[Math.floor(Math.random() * types.length)];
  }
}

const contentType = determineContentType(marketData);

// Generate prompts based on content type
const prompts = {
  volatility_analysis: `Analyze the current market volatility with NIFTY at ${marketData[0].price} showing ${marketData[0].change_percent}% change. Focus on options strategies for volatile markets following Abid Hassan's methodology. Include specific strike prices and risk management.`,

  market_open_analysis: `Create a market opening analysis for Indian markets. NIFTY: ${marketData[0].price}, BANKNIFTY: ${marketData[1]?.price}, SENSEX: ${marketData[2]?.price}. Highlight key levels, options OI data, and potential intraday strategies.`,

  market_close_summary: `Summarize today's market action with closing data. Analyze the day's price action, volume patterns, and prepare outlook for tomorrow. Include options chain analysis.`,

  weekly_outlook: `Generate a comprehensive weekly outlook for Indian markets. Include technical analysis, event calendar, and options strategies for the week. Focus on NIFTY and BANKNIFTY levels.`,

  educational_options: `Create educational content about options trading concepts. Make it practical with current market examples. Topic: ${['Greeks', 'IV Crush', 'Options Spreads', 'Risk Management'][Math.floor(Math.random() * 4)]}`,

  options_strategy: `Explain an options strategy suitable for current market conditions. Include entry/exit rules, risk-reward ratios, and adjustment techniques as per Abid Hassan's approach.`,

  sector_focus: `Analyze a specific sector's performance and options opportunities. Include top stocks, sectoral rotation insights, and hedging strategies.`,

  risk_management: `Create content on risk management in options trading. Include position sizing, stop-loss strategies, and portfolio hedging techniques.`
};

return {
  contentType,
  prompt: prompts[contentType],
  marketData: marketData[0],
  timestamp: new Date().toISOString()
};

// Format content for different platforms
const content = $json.choices[0].message.content;
const marketData = $node['generate-prompt'].json.marketData;

// LinkedIn Version (Long-form)
const linkedinContent = {
  platform: 'linkedin',
  content: content,
  hashtags: '#IndianStockMarket #NIFTY #OptionsTrading #StockMarket #Trading #FinancialMarkets #InvestmentStrategy',
  disclaimer: '\n\nðŸ“Š Disclaimer: This is for educational purposes only. Not investment advice. Please consult your financial advisor.',
  length: content.length
};

// Twitter Version (Thread)
function createTwitterThread(text) {
  const sentences = text.match(/[^.!?]+[.!?]+/g) || [];
  const tweets = [];
  let currentTweet = '';

  sentences.forEach(sentence => {
    if ((currentTweet + sentence).length <= 250) {
      currentTweet += sentence;
    } else {
      if (currentTweet) tweets.push(currentTweet.trim());
      currentTweet = sentence;
    }
  });

  if (currentTweet) tweets.push(currentTweet.trim());

  // Add thread numbering
  return tweets.map((tweet, i) => ({
    content: `${i > 0 ? `${i+1}/` : ''}${tweets.length > 1 ? `${tweets.length} ` : ''}${tweet}`,
    isThread: tweets.length > 1,
    position: i + 1
  }));
}

const twitterContent = {
  platform: 'twitter',
  thread: createTwitterThread(content.substring(0, 1000)),
  hashtags: '#NIFTY #OptionsTrading #StockMarket',
  metrics: `NIFTY: ${marketData.price} (${marketData.change_percent}%)`
};

// Telegram Version (Concise)
const telegramContent = {
  platform: 'telegram',
  content: content.substring(0, 500) + '...',
  metrics: `
ðŸ“ˆ NIFTY: ${marketData.price} (${marketData.change_percent}%)
ðŸ“Š Volume: ${(marketData.volume/1000000).toFixed(2)}M`,
  disclaimer: 'âš ï¸ Educational content only. Not investment advice.',
  readMore: 'Full analysis on LinkedIn'
};

return [
  linkedinContent,
  twitterContent,
  telegramContent
];

// Check content uniqueness
const newContent = $node['format-platforms'].json;
const existingHashes = $json.map(row => row.content_hash);

const crypto = require('crypto');

function generateHash(text) {
  return crypto.createHash('md5').update(text).digest('hex');
}

// Check each platform's content
const results = newContent.map(item => {
  const contentText = item.platform === 'twitter' 
    ? item.thread.map(t => t.content).join(' ')
    : item.content;

  const hash = generateHash(contentText.substring(0, 200));
  const isDuplicate = existingHashes.includes(hash);

  return {
    ...item,
    contentHash: hash,
    isDuplicate: isDuplicate,
    shouldPost: !isDuplicate
  };
});

return results.filter(item => item.shouldPost);
